mapper.update:
update unread_message_count
   set count = count+1
 where room_id = #{roomId} and receiver_id != #{receiverId}

update unread_message_count
   set count = 0
 where room_id = #{roomId} and receiver_id = #{receiverId}

mapper.select:
select count
  from unread_message_count
 where room_id = #{roomId} and receiver_id = #{receiverId}
